00413 bool DIOSPITFTDISPLAYILI9341::TFT_SetWindow(int xs, int ys, int xe, int ye)
00414 {
00415   bool status;
00416 
00417   /* Column address set */
00418   status = WriteRegister(0x2A, 4,  (xs >> 8) & 0xFF, xs & 0xFF, (xe >> 8) & 0xFF, xe & 0xFF);
00419 
00420   /* Row adress set */
00421   if(status) status = WriteRegister(0x2B, 4,  (ys >> 8) & 0xFF, ys & 0xFF, (ye >> 8) & 0xFF, ye & 0xFF);
00422 
00423   return status;
00424 }
