00109 bool DIOSPITFTDISPLAYST7789::IniDevice()
00110 {
00111   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC) != DIOGPIO_ID_NOTDEFINED)
00112     {
00113       GEN_DIOGPIO.SetMode(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)         , DIOGPIO_MODE_OUTPUT);      
00114       GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)        , false);
00115       
00116     }
00117 
00118   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_BACKLIGHT) != DIOGPIO_ID_NOTDEFINED)
00119     {
00120       GEN_DIOGPIO.SetMode(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_BACKLIGHT)  , DIOGPIO_MODE_OUTPUT);
00121       GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_BACKLIGHT) , false);
00122     }
00123 
00124   if(!DIODEVICESPI::IniDevice()) return false;
00125 
00126   diostreamcfg->SetSPIMode(DIOSTREAMSPI_MODE_3);
00127   diostreamcfg->SetNBitsWord(8);
00128   diostreamcfg->SetSpeed(16*1000*1000);
00129   diostreamcfg->SetDelay(0);
00130   diostreamcfg->SetIsOnlyWrite(false);
00131  
00132   if(diostream->Open())
00133     {
00134       if(diostream->WaitToConnected(timeout))
00135         {     
00136           if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) != DIOGPIO_ID_NOTDEFINED)
00137             {
00138               GEN_DIOGPIO.SetMode(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)      , DIOGPIO_MODE_OUTPUT);
00139 
00140               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , true);    GEN_XSLEEP.MilliSeconds(50);
00141               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , false);   GEN_XSLEEP.MilliSeconds(50);
00142               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , true);    GEN_XSLEEP.MilliSeconds(50);    
00143             }              
00144 
00145           SetActiveBlackLight(true);
00146 
00147           if(!TFT_Init()) return false;
00148 
00149           return DIODEVICE::Ini();
