00169 bool DIOSPITFTDISPLAYST7789::Clear(XWORD color)
00170 {
00171   int size = GetSizeBuffer();
00172 
00173   if(!TFT_SetWindow(0, 0, width, height)) return false;
00174 
00175   SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_RAMWR);  // write to RAM
00176 
00177   XBYTE chigh = (XBYTE)(color >> 8);
00178   XBYTE clow  = (XBYTE)(color &  0x00FF);
00179 
00180   canvasbuffer.Delete();
00181   canvasbuffer.Resize(size);
00182   canvasbuffer.ResetPosition();
00183 
00184   int c = 0;
00185  
00186   do{ canvasbuffer.Set(chigh);  c++;
00187       canvasbuffer.Set(clow);   c++;
00188 
00189     } while(c < size);
00190 
00191   return SendData();
00192 }
