00293 bool DIOSPITFTDISPLAYST7789::TFT_Init()
00294 {
00295   _colstart  = 0;
00296   _rowstart  = 0;
00297   _xstart    = 0;
00298   _ystart    = 0;
00299 
00300   //TFT_Reset();
00301 
00302   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_SWRESET)) return false;                         // Software reset
00303   GEN_XSLEEP.MilliSeconds(150);
00304 
00305   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_SLPOUT))  return false;                         // Out of sleep mode
00306   GEN_XSLEEP.MilliSeconds(500);
00307 
00308   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_COLMOD,  1, 0x55)) return false;          // Set color mode   0x55 (16-bit color)
00309   GEN_XSLEEP.MilliSeconds(10);
00310 
00311   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL  , 1, 0x00)) return false;         // Memory access ctrl (directions) 0x00 Row addr/col addr, bottom to top refresh
00312 
00313   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_CASET   , 4                               // Column addr set
00314                                                                   , 0x00                            // XSTART = 0
00315                                                                   , DIOSPITFTDISPLAYST7789_240x240_XSTART
00316                                                                   , (DIOSPITFTDISPLAYST7789_TFTWIDTH + DIOSPITFTDISPLAYST7789_240x240_XSTART) >> 8
00317                                                                   , (DIOSPITFTDISPLAYST7789_TFTWIDTH + DIOSPITFTDISPLAYST7789_240x240_XSTART) & 0xFF)) return false;   // XEND = 240
00318   
00319   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_RASET   , 4                               // Row addr set
00320                                                                   , 0x00                            // YSTART = 0
00321                                                                   , DIOSPITFTDISPLAYST7789_240x240_YSTART
00322                                                                   , (DIOSPITFTDISPLAYST7789_TFTHEIGHT + DIOSPITFTDISPLAYST7789_240x240_YSTART) >> 8
00323                                                                   , (DIOSPITFTDISPLAYST7789_TFTHEIGHT + DIOSPITFTDISPLAYST7789_240x240_YSTART) & 0xFF)) return false;   // YEND = 240
00324 
00325 
00326 
00327   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_INVON)) return false;                           // Inversion ON
00328   GEN_XSLEEP.MilliSeconds(10);
00329 
00330   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_NORON)) return false;                           // Normal display on
00331   GEN_XSLEEP.MilliSeconds(10);
00332 
00333   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_DISPON)) return false;                          // Main display turn on, no args, w/delay
00334   GEN_XSLEEP.MilliSeconds(20);
00335 
00336   TFT_SetRotation(1); 
00337 
00338   return true;
00339 }
