00434 void DIOSPITFTDISPLAYST7789::TFT_SetRotation(XBYTE mode) 
00435 {
00436   SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL);
00437   
00438   rotation = (mode & 3);
00439   
00440   switch(rotation) 
00441     {
00442       case 0: SendData((XBYTE)(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MX | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MY | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_RGB));
00443               _xstart = _colstart;
00444               _ystart = _rowstart;
00445               break;
00446 
00447       case 1: SendData((XBYTE)(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MY | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MV | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_RGB));
00448               _ystart = _colstart;
00449               _xstart = _rowstart;
00450               break;
00451 
00452       case 2: SendData((XBYTE)(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_RGB));
00453               _xstart = 0;
00454               _ystart = 0;
00455               break;
00456 
00457       case 3: SendData((XBYTE)(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MX | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_MV | DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL_RGB));
00458               _xstart = 0;
00459               _ystart = 0;
00460               break;
