00613 void HAL_I2C_MasterRxCpltCallback(I2C_HandleTypeDef* hi2c)
00614 {
00615   int indexport = -1;
00616 
00617   if(hi2c->Instance == I2C1) indexport = 0;
00618   //if(hi2c->Instance == I2C2) indexport = 1;
00619 
00620   for(int c=0; c<DIOSTM32STREAMI2C_MAXHANDLES; c++)
00621     {
00622       if(DIOSTM32STREAMI2C::handles[c])
00623         {
00624           if(DIOSTM32STREAMI2C::handles[c]->indexport  == indexport)
00625             {
00626               if(DIOSTM32STREAMI2C::handles[c]->GetConnectStatus() == DIOSTREAMSTATUS_CONNECTED)
00627                 {
00628                   if(!DIOSTM32STREAMI2C::handles[c]->IsBlockRead())
00629                     {
00630                       if(!DIOSTM32STREAMI2C::handles[c]->inbuffer->IsBlocked())
00631                         {
00632                           if(DIOSTM32STREAMI2C::handles[c]->readcache[indexport].GetSize())
00633                             {
00634                               DIOSTM32STREAMI2C::handles[c]->inbuffer->Add(DIOSTM32STREAMI2C::handles[c]->readcache);
00635                               DIOSTM32STREAMI2C::handles[c]->readcache[indexport].Delete();
00636                             }
00637 
00638                           DIOSTM32STREAMI2C::handles[c]->inbuffer->Add(DIOSTM32STREAMI2C::handles[c]->bufferdata, DIOSTM32STREAMI2C::handles[c]->sizebufferdata);
00639                         }
00640                        else
00641                         {
00642                           DIOSTM32STREAMI2C::handles[c]->readcache[indexport].Add(DIOSTM32STREAMI2C::handles[c]->bufferdata, DIOSTM32STREAMI2C::handles[c]->sizebufferdata);
00643                         }
00644                     }
00645                 }
00646 
00647               DIOSTM32STREAMI2C::handles[c]->sizebufferdata = 0;
00648             }
00649         }
00650     }
