00696 void DIOSTM32STREAMI2C::ThreadConnection(void* param)
00697 {
00698   DIOSTM32STREAMI2C* diostream = (DIOSTM32STREAMI2C*)param;
00699   if(!diostream) return;
00700 
00701   if(diostream->GetEvent()==DIOSTREAMI2C_FSMEVENT_NONE)
00702     {
00703       switch(diostream->GetCurrentState())
00704         {
00705           case DIOSTREAMI2C_FSMSTATE_NONE                   : break;
00706 
00707           case DIOSTREAMI2C_FSMSTATE_CONNECTED              : break;
00708 
00709           case DIOSTREAMI2C_FSMSTATE_WAITINGTOREAD          : if(!diostream->IsBlockRead())
00710                                                                 {
00711                                                                   if(!diostream->inbuffer->IsBlocked())
00712                                                                     {
00713                                                                       if(diostream->readcache[diostream->indexport].GetSize())
00714                                                                         {
00715                                                                           diostream->inbuffer->Add(diostream->readcache[diostream->indexport]);
00716                                                                           diostream->readcache[diostream->indexport].Delete();
00717                                                                         }
00718                                                                     }
00719                                                                 }
00720 
00721                                                               if(!diostream->IsBlockWrite())
00722                                                                 {
00723                                                                   if(!diostream->outbuffer->IsBlocked())
00724                                                                     {
00725                                                                       int esize = diostream->outbuffer->GetSize();
00726                                                                       if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00727 
00728                                                                       if(esize)
00729                                                                         {
00730                                                                           diostream->outbuffer->Extract(diostream->writebuffer, 0, esize);
00731                                                                           XDWORD _esize = diostream->WriteDirect(diostream->writebuffer, esize);
00732                                                                           if(_esize != esize)
00733                                                                             {
00734                                                                               diostream->outbuffer->Add(diostream->writebuffer, esize);
00735                                                                             }                                                                        
00736                                                                         }
00737                                                                     }
00738                                                                 }
00739                                                               break;
00740 
00741           case DIOSTREAMI2C_FSMSTATE_DISCONNECTING          : break;
00742 
00743         }
00744     }
00745    else
00746     {
00747       if(diostream->GetEvent()<DIOSTREAMI2C_LASTEVENT)
00748         {
00749           diostream->CheckTransition();
00750 
00751           switch(diostream->GetCurrentState())
00752             {
00753               case DIOSTREAMI2C_FSMSTATE_NONE               : break;
00754 
00755               case DIOSTREAMI2C_FSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00756                                                                 xevent.SetDIOStream(diostream);
00757                                                                 diostream->PostEvent(&xevent);
00758 
00759                                                                 diostream->SetEvent(DIOSTREAMI2C_FSMEVENT_WAITINGTOREAD);
00760                                                               }
00761                                                               break;
00762 
00763               case DIOSTREAMI2C_FSMSTATE_WAITINGTOREAD      : break;
00764 
00765               case DIOSTREAMI2C_FSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00766                                                                 xevent.SetDIOStream(diostream);
00767                                                                 diostream->PostEvent(&xevent);
00768 
00769                                                                 diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00770                                                               }
00771                                                               break;
00772             }
00773         }
00774     }
00775 }
