00777 bool DIOSCRAPERWEB::ExtractString(XSTRING& searchini, XSTRING& searchend, XBUFFER* xbuffer, bool usebufferpos, XSTRING& result)
00778 {
00779   result.Empty();
00780 
00781   if(!xbuffer)            return false;
00782 
00783   if(searchini.IsEmpty()) return false;
00784   if(searchend.IsEmpty()) return false;
00785 
00786   XSTRING html;
00787   int        indexini;
00788   int        indexend;
00789 
00790   //html = (char*)xbuffer->Get();
00791   html = *xbuffer; // #Imanol, changed to avoid a buffer overrun
00792 
00793   indexini = html.Find(searchini,false,usebufferpos?xbuffer->GetPosition():0);
00794   if(indexini==XSTRING_NOTFOUND) return false;
00795 
00796   indexend = html.Find(searchend,false,indexini + searchini.GetSize());
00797   if(indexend==XSTRING_NOTFOUND) return false;
00798 
00799   if((int)(indexini + searchini.GetSize())>=indexend) return false;
00800 
00801   html.Copy(indexini + searchini.GetSize(),indexend,result);
00802 
00803   result.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
00804   result.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
00805 
00806   if(result.IsEmpty()) return false;
00807 
00808   return true;
00809 }
