00385 bool DIOSTREAMUDP::ReadDatagram(XSTRING& address, XWORD& port, XBUFFER& xbuffer)
00386 {
00387   int index = GetFirstDatagram(false);
00388   if(index != DIOSTREAMUDP_NOTFOUND)
00389     {
00390       DIOSTREAMUDPDATAGRAM* datagram = GetDatagram(index);
00391       if(datagram)
00392         {
00393           address = datagram->GetAddress()->Get();
00394           port    = datagram->GetPort();
00395 
00396           xbuffer.Delete();
00397           xbuffer.Add(datagram->GetData());
00398 
00399           nbytesread+= datagram->GetData()->GetSize();
00400           if(xtimernotactivity) xtimernotactivity->Reset();
00401 
00402           DeleteDatagram(index);
00403 
00404           return true;
00405         }
00406     }
00407 
00408   return false;
00409 }
