00815 int DIOSTREAMUDP::GetFirstDatagram(bool tosend)
00816 {
00817   int index = DIOSTREAMUDP_NOTFOUND;
00818 
00819   if(datagramsmutex)  datagramsmutex->Lock();
00820 
00821   for(int c=0;c<(int)datagrams.GetSize();c++)
00822     {
00823       DIOSTREAMUDPDATAGRAM* datagram = (DIOSTREAMUDPDATAGRAM*)datagrams.Get(c);
00824       if(datagram)
00825         {
00826           if(datagram->IsToSend() == tosend)
00827             {
00828               index = c;
00829               break;
00830             }
00831         }
00832     }
00833 
00834   if(datagramsmutex)  datagramsmutex->UnLock();
00835 
00836   return index;
00837 }
