00311 bool DIOSTREAMUDPACKNOWLEDGE::WriteDatagram(XSTRING& address, XWORD port, XBYTE* buffer, XDWORD size)
00312 {
00313   XSTRING old_address;
00314   int     old_port;
00315 
00316   old_address   = addressremote.Get();
