00910 bool DIOURL::GetExtension(XSTRING& extension)
00911 {
00912   extension.Empty();
00913 
00914   int indexext;
00915   for(indexext = GetSize(); indexext>0; indexext--)
00916     {
00917       if(Get()[indexext] == __C('.'))
00918         {
00919           break;
00920         }
00921 
00922       if((Get()[indexext] == __C('\\')) ||
00923           Get()[indexext] == __C('/')   ||
00924           Get()[indexext] == __C(':'))
00925         {
00926           indexext = -1;
00927           break;
00928         }
00929     }
00930 
00931   if(indexext > 0) Copy(indexext, extension);
00932 
00933   return (extension.IsEmpty()?false:true);
00934 }
