00206 bool DIOWINDOWSSTREAMBLUETOOTH::Close()
00207 {
00208   if(!threadconnection) return false;
00209 
00210   threadconnection->End();
00211 
00212   if(config->IsServer())
00213     {
00214       if(addr)
00215         {
00216           SDP_RegisterService(false,(char*)config->GetServerPropertys(0),
00217                                     (char*)config->GetServerPropertys(1),
00218                                     (char*)config->GetServerPropertys(2),
00219                                     (void*)addr);
00220 
00221           delete (SOCKADDR_BTH*)addr;
00222           addr = NULL;
00223         }
00224 
00225       if(handleserver!=INVALID_SOCKET)
00226         {
00227           shutdown(handleserver,SD_BOTH);
00228           closesocket(handleserver);
00229           handleserver  = INVALID_SOCKET;
00230         }
00231     }
00232 
00233 
00234   if(handlesocket!=INVALID_SOCKET)
00235     {
00236       DWORD nonblock = 0;
00237       if(ioctlsocket(handlesocket, FIONBIO, &nonblock) != SOCKET_ERROR)
00238         {
00239           shutdown(handlesocket,SD_BOTH);
00240           closesocket(handlesocket);
00241 
00242           handlesocket  = INVALID_SOCKET;
00243         }
00244     }
00245 
00246   return true;
