00261 int DIOWINDOWSSTREAMBLUETOOTH::IsReadyConnect(SOCKET socket)
00262 {
00263   struct timeval  tv;
00264   SOCKET          rc;
00265   fd_set          fdr;
00266   fd_set          fdw;
00267   fd_set          fds;
00268 
00269   if(socket==INVALID_SOCKET) return -1;
00270 
00271   FD_ZERO(&fdr);
00272   FD_ZERO(&fdw);
00273   FD_ZERO(&fds);
00274 
00275   FD_SET((unsigned int)socket,&fdr);
00276   FD_SET((unsigned int)socket,&fdw);
00277   FD_SET((unsigned int)socket,&fds);
00278 
00279   tv.tv_sec  = 0;
00280   tv.tv_usec = 100;
00281 
00282   rc = select((int)socket+1, &fdr, &fdw, &fds, &tv);
00283   if(rc==SOCKET_ERROR) return -1;
00284   //if(rc>1)             return -1;
00285 
00286   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00287   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00288   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00289 
00290   if(status3) return -1;
00291   if(((status1) || (status2)) && (rc==1))  return  1;
00292 
00293   return 0;
00294 }
