01213       sendheader.AddFormat(__L("nonce=\"%s\", "), nonce_str.Get());           
01214       sendheader.AddFormat(__L("uri=\"%s\", "), resource.Get());     
01215       sendheader.AddFormat(__L("algorithm=%s, "), algorithm_str.Get());
01216       sendheader.AddFormat(__L("response=\"%s\", "), response.Get());   
01217       sendheader.AddFormat(__L("qop=%s, "), auth_str.Get());     
01218       sendheader.AddFormat(__L("nc=%s, "), nc_str.Get());     
01219       sendheader.AddFormat(__L("cnonce=\"%s\" "), cnonce_str.Get());   
01220       if(opaque_str.GetSize()) sendheader.AddFormat(__L(", opaque=\"%s\""), opaque_str.Get());           
01221       sendheader += __L("\r\n");
01222 
01223       sendheader += __L("\r\n");
01224       
01225       //XTRACE_PRINTCOLOR(XTRACE_COLOR_PURPLE, __L("%s"), sendheader.Get());
01226 
01227       diostream->WriteStr(sendheader);
01228 
01229       xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_WRITEHEADER);
01230       PostEvent(&xevent);
01231 
01232       //--- Send Post data -------------------------
01233 
01234       if(postdata)
01235         {
01236           diostream->Write(postdata->Get(), postdata->GetSize());
01237 
01238           xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_SENDPOSTDATA);
01239           PostEvent(&xevent);
01240         }
01241 
01242       diostream->WaitToFlushOutXBuffer(timeout);
01243 
01244       //--- Read Header ----------------------------
01245 
01246       if(!header.Read(diostream, timeout))
01247         {
01248           diostream->Close();
01249           return false;
01250         }
01251     }
01252 
01253 
01254   if((header.GetResultServer()>=400)||(!header.GetResultServer()))
01255     {     
01256       xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_HEADERERROR);
01257       PostEvent(&xevent);
01258 
01259       if(IsActiveDoStopHTTPError())
01260         {
01261           diostream->Close();
01262           return false;
01263         }
01264     }
01265 
01266   XQWORD   contentlength;
01267   XSTRING  etag;
01268 
01269   contentlength = header.GetContentLength();
01270   header.GetETag(etag);
01271 
01272   xevent.SetContentLenght(contentlength);
01273   xevent.GetEtag()->Set(etag);
01274 
01275   xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_READHEADER);
01276 
01277   PostEvent(&xevent);
01278 
01279   //--- Read Content ---------------------------
01280 
01281   XBUFFER* _tobuffer     = NULL;
01282   XFILE*   _tofile       = NULL;
01283  
01284   XTIMER* GEN_XFACTORY_CREATE(timerdownload, CreateTimer())
01285   if(!timerdownload)
01286     {     
01287       diostream->Close();
01288       return false;
01289     }
01290        
01291   if(istobuffer)
01292     {
01293       _tobuffer = (XBUFFER*)to;
01294       if(_tobuffer) _tobuffer->Delete();
01295     }
01296    else
01297     {
01298       _tofile = (XFILE*)to;
01299     }
01300 
01301   XBYTE* buffer = new XBYTE[maxsizebuffer];
01302   if(buffer)
01303     {
01304       XQWORD  totalsizeread = 0;
01305       int     br;
01306 
01307       timerout->Reset();
01308 
01309       do{ memset(buffer, 0, maxsizebuffer);
01310 
01311           br = diostream->Read(buffer, maxsizebuffer);
01312           if(br)
01313             {
01314               timerout->Reset();
01315 
01316               if(istobuffer)
01317                 {
01318                   if(_tobuffer) _tobuffer->Add(buffer, br);
01319                 }
01320                else
01321                 {
01322                   if(_tofile) _tofile->Write(buffer, br);
01323                 }
01324 
01325               totalsizeread += br;
01326 
01327               xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_READBODYBLOCK);
01328               xevent.SetDownloadSize(totalsizeread);
01329               xevent.SetContentLenght((XQWORD)contentlength);
01330 
01331               if(contentlength) xevent.SetOperationPercent((float)((totalsizeread * 100.0f) / contentlength));
01332 
01333               if(timerdownload->GetMeasureSeconds()) xevent.SetMBPSSpeed((XDWORD)(((totalsizeread * 8) / timerdownload->GetMeasureSeconds()) / 1000));
01334 
01335               PostEvent(&xevent);
01336             }
01337            else
01338             {
01339               if(timerout->GetMeasureSeconds()>(XDWORD)timeout)
01340                 {
01341                   if(!totalsizeread) status = false;
01342                   break;
01343                 }
01344 
01345               if(diostream->GetConnectStatus() != DIOSTREAMSTATUS_CONNECTED)
01346                 {
01347                   if(!diostream->GetInXBuffer()->GetSize())
01348                     {
01349                       break;
01350                     }
01351                 }
01352             }
01353 
01354         } while(1);
01355 
01356       delete [] buffer;
01357     }
01358 
01359   GEN_XFACTORY.DeleteTimer(timerdownload);
01360 
01361   //--- Close ----------------------------------
01362 
01363   diostream->Close();
01364 
01365   xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_CLOSEWEB);
01366   PostEvent(&xevent);
01367 
01368   return status;
01369 }
