01386 bool DIOWEBCLIENT::GetSubStringWWWWAuthenticate(XSTRING& www_authenticate, XCHAR* field, XSTRING& value, bool betweenquotation)
01387 {
01388   if(www_authenticate.IsEmpty()) return false;
01389 
01390   XSTRING sfield;
01391 
01392   sfield = field;
01393 
01394   int index = www_authenticate.Find(field, true);
01395   if(index == XSTRING_NOTFOUND) return false;
01396   index +=  sfield.GetSize();
01397 
01398   index = www_authenticate.Find(__L("="), true, index);
01399   if(index == XSTRING_NOTFOUND) return false;
01400 
01401   int start = 0;
01402   int end   = 0;
01403 
01404   if(betweenquotation)
01405     {
01406       start = www_authenticate.Find(__L("\""), true, index+1);
01407       if(start == XSTRING_NOTFOUND) return false;
01408 
01409       end = www_authenticate.Find(__L("\""), true, start+1);
01410       if(end == XSTRING_NOTFOUND) return false;
01411     }
01412    else
01413     { 
01414       start = index;
01415 
01416       end = www_authenticate.Find(__L(","), true, start+1);
01417       if(end == XSTRING_NOTFOUND) return false;
01418     }
01419 
01420   return www_authenticate.Copy(start+1, end, value)?true:false;
01421 }
