00777 bool DIOWEBPAGEHTMLCREATOR::AddFile(XPATH& xpath)
00778 {
00779   XFILETXT filetxt;
00780   bool     status = true;
00781   bool     intag  = false;
00782 
00783   if(!filetxt.Open(xpath)) return false;
00784 
00785   if(filetxt.ReadAllFile())
00786     {
00787       Empty();
00788 
00789       XBUFFER LFdata;
00790 
00791       filetxt.GetLF(LFdata);
00792 
00793       for(int c=0;c<filetxt.GetNLines();c++)
00794         {
00795           XSTRING* line = filetxt.GetLine(c);
00796           if(line)
00797             {
00798               XSTRING inioutput;
00799               XSTRING endoutput;
00800               int     result;
00801 
00802               result = CheckXTAG(line, intag, inioutput, endoutput);
00803               switch(result)
00804                 {
00805                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_NOTFOUND   : { if(!line->IsEmpty())
00806                                                                               {
00807                                                                                 if(Add(*line)) Add(filetxt.GetLF()->Get());
00808                                                                               }
00809                                                                           }
00810                                                                           break;
00811 
00812                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND   : { if(!inioutput.IsEmpty())
00813                                                                               {
00814                                                                                 if(Add(inioutput)) Add(filetxt.GetLF()->Get());
00815                                                                               }
00816 
00817                                                                             DIOWEBPAGEHTMLCREATOR_XEVENT xevent(this, DIOWEBPAGEHTMLCREATOR_XEVENT_TYPE_XTAGFOUND);
00818 
00819                                                                             xevent.SetPageHTMLCreator(this);
00820                                                                             xevent.SetXTAG(&lastXTAG);
00821 
00822                                                                             PostEvent(&xevent);
00823 
00824                                                                             if(!endoutput.IsEmpty())
00825                                                                               {
00826                                                                                 if(Add(endoutput)) Add(filetxt.GetLF()->Get());
00827                                                                               }
00828                                                                           }
00829                                                                           break;
00830 
00831                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND: { if(!inioutput.IsEmpty())
00832                                                                               {
00833                                                                                 if(Add(inioutput)) Add(filetxt.GetLF()->Get());
00834                                                                               }
00835 
00836                                                                             if(!endoutput.IsEmpty())
00837                                                                               {
00838                                                                                 if(Add(endoutput)) Add(filetxt.GetLF()->Get());
00839                                                                               }
00840                                                                           }
00841                                                                           break;
00842                 }
00843             }
00844         }
00845     }
00846 
00847   filetxt.Close();
00848 
