00867 DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS DIOWEBPAGEHTMLCREATOR::CheckXTAG(XSTRING* originalline,bool& intag, XSTRING& inioutputline,XSTRING& endoutputline)
00868 {
00869   int index[3];
00870 
00871   inioutputline.Empty();
00872   endoutputline.Empty();
00873 
00874   if(!intag)
00875     {
00876       index[0] = originalline->Find(__L("<?"), true);
00877       if(index[0]!=XSTRING_NOTFOUND)
00878         {
00879           index[1] = originalline->Find(DIOWEBPAGEHTMLCREATOR_TAGXENGINE, true);
00880           if(index[1]!=XSTRING_NOTFOUND)
00881             {
00882               lastXTAG.Empty();
00883 
00884               originalline->Copy(0,index[0],inioutputline);
00885 
00886               index[2] = originalline->Find(__L("?>"),true);
00887               if(index[2]!=XSTRING_NOTFOUND)
00888                 {
00889                   XSTRING endline;
00890 
00891                   originalline->Copy(index[1]+3,index[2],lastXTAG);
00892                   originalline->Copy(index[2]+2,endoutputline);
00893 
00894                   return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND;
00895 
00896                 }
00897                else
00898                 {
00899                   originalline->Copy(index[1]+3,lastXTAG);
00900                   intag = true;
00901                   return  DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND;
00902                 }
00903             }
00904         }
00905 
00906       inioutputline = originalline->Get();
00907     }
00908    else
00909     {
00910       index[0] = originalline->Find(__L("?>"),true);
00911       if(index[0]!=XSTRING_NOTFOUND)
00912         {
00913           XSTRING TAGstring;
00914 
00915           originalline->Copy(0,index[0],TAGstring);
00916           lastXTAG+=TAGstring.Get();
00917 
00918           originalline->Copy(index[0]+2,endoutputline);
00919 
00920           intag = false;
00921 
00922           return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND;
00923         }
00924 
00925       lastXTAG += originalline->Get();
00926 
00927       inioutputline.Empty();
00928       endoutputline.Empty();
00929 
00930       return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND;
00931     }
00932 
00933   return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_NOTFOUND;
00934 }
