00199 bool DIOWEBSERVER_HEADER::CreateContentType(XSTRING* resource, XSTRING& contenttype)
00200 {
00201   contenttype.Empty();
00202 
00203        if(resource->Find(__L(".htm")  , true) != XSTRING_NOTFOUND)  contenttype = __L("text/html");
00204   else if(resource->Find(__L(".xml")  , true) != XSTRING_NOTFOUND)  contenttype = __L("text/xml");
00205   else if(resource->Find(__L(".exe")  , true) != XSTRING_NOTFOUND)  contenttype = __L("application/octet-stream");
00206   else if(resource->Find(__L(".gif")  , true) != XSTRING_NOTFOUND)  contenttype = __L("image/gif");
00207   else if(resource->Find(__L(".jpg")  , true) != XSTRING_NOTFOUND)  contenttype = __L("image/jpeg");
00208   else if(resource->Find(__L(".jpeg") , true) != XSTRING_NOTFOUND)  contenttype = __L("image/jpeg");
00209   else if(resource->Find(__L(".png")  , true) != XSTRING_NOTFOUND)  contenttype = __L("image/png");
00210   else if(resource->Find(__L(".wav")  , true) != XSTRING_NOTFOUND)  contenttype = __L("audio/x-wav");
00211   else if(resource->Find(__L(".mp3")  , true) != XSTRING_NOTFOUND)  contenttype = __L("audio/mp3");
00212   else if(resource->Find(__L(".gz")   , true) != XSTRING_NOTFOUND)  contenttype = __L("application/x-gzip");
00213   else if(resource->Find(__L(".mpg")  , true) != XSTRING_NOTFOUND)  contenttype = __L("video/mpeg");
00214   else if(resource->Find(__L(".pdf")  , true) != XSTRING_NOTFOUND)  contenttype = __L("application/pdf");
00215   else if(resource->Find(__L(".avi")  , true) != XSTRING_NOTFOUND)  contenttype = __L("video/x-msvideof");
00216   else if(resource->Find(__L(".ogv")  , true) != XSTRING_NOTFOUND)  contenttype = __L("video/ogg");
00217   else if(resource->Find(__L(".ogg")  , true) != XSTRING_NOTFOUND)  contenttype = __L("video/ogg");
00218   else if(resource->Find(__L(".css")  , true) != XSTRING_NOTFOUND)  contenttype = __L("text/css");
00219   else if(resource->Find(__L(".js")   , true) != XSTRING_NOTFOUND)  contenttype = __L("application/javascript");
00220   else if(resource->Find(__L(".json") , true) != XSTRING_NOTFOUND)  contenttype = __L("application/json");
00221   else if(resource->Find(__L(".zip")  , true) != XSTRING_NOTFOUND)  contenttype = __L("application/zip");
00222   else if(resource->Find(__L(".otf")  , true) != XSTRING_NOTFOUND)  contenttype = __L("application/x-font-otf");
00223 
00224   if(!resource->Compare(__L("/") ,  true))  contenttype = __L("text/html");
00225 
00226   return true;
00227 }
