00248 bool DIOWEBSERVER_HEADER::Create(XSTRING* resource, int version, int subversion, DIOWEBHEADER_RESULT result, int size, XSTRING* entitytag, bool isplayable, bool password)
00249 {
00250   /*
00251       Accept - Ranges:bytes
00252       Connection : Keep - Alive
00253       Content - Length : 6
00254       Content - Type : text / html
00255       Date : Mon, 06 Nov 2017 11 : 21 : 24 GMT
00256       ETag : "6-55d4e3b3b9f20"
00257       Keep - Alive : timeout = 5, max = 93
00258       Last - Modified : Mon, 06 Nov 2017 10 : 51 : 06 GMT
00259       Server : Apache / 2.4.23 (Win64)PHP / 5.6.25
00260   */
00261 
00262   XSTRING line;
00263   line.Format(__L("HTTP/%d.%d %d "), version, subversion, result);
00264 
00265   if(password)
00266     {
00267       line += __L("Unauthorised");
00268       AddLine(line);
00269       AddLine(__L("WWW-Authenticate: Basic"));
00270     }
00271    else
00272     {
00273       XSTRING description;
00274 
00275       GetDescriptionResult(result, description);
00276 
00277       line += description;
00278       AddLine(line);
00279     }
00280 
00281 
00282   AddLine(__L("Accept-Ranges: bytes"));
00283 
00284   line.Empty();
00285   CreateContentType(resource, line);
00286 
00287   if(!line.IsEmpty())
00288     {
00289       line.Insert(__L("Content-Type: "), 0);
00290       AddLine(line.Get());
00291     }
00292 
00293   XSTRING date;
00294   XDATETIME* GEN_XFACTORY_CREATE(datetime, CreateDateTime())
00295   if(datetime)
00296     {
00297       XSTRING day;
00298       XSTRING month;
00299       XSTRING hour;
00300 
00301       datetime->Read();
00302       datetime->GetDateTimeToString(XDATETIME_FORMAT_ADDTIME | XDATETIME_FORMAT_TIMEWITHSECONDS, hour);
00303       datetime->GetMonthString(month);
00304 
00305       switch((int)datetime->GetDayOfWeek())
00306         {
00307           case  1:  day.Set(__L("Mon")); break;
00308           case  2:  day.Set(__L("Tue")); break;
00309           case  3:  day.Set(__L("Wed")); break;
00310           case  4:  day.Set(__L("Thu")); break;
00311           case  5:  day.Set(__L("Fri")); break;
00312           case  6:  day.Set(__L("Sat")); break;
00313           case  0:
00314           case  7:  day.Set(__L("Sun")); break;
00315           default:  break;
00316         }
00317 
00318       switch(datetime->GetMonth())
00319         {
00320           case  1:  month.Set(__L("Jan")); break;
00321           case  2:  month.Set(__L("Feb")); break;
00322           case  3:  month.Set(__L("Mar")); break;
00323           case  4:  month.Set(__L("Apr")); break;
00324           case  5:  month.Set(__L("May")); break;
00325           case  6:  month.Set(__L("Jun")); break;
00326           case  7:  month.Set(__L("Jul")); break;
00327           case  8:  month.Set(__L("Aug")); break;
00328           case  9:  month.Set(__L("Sep")); break;
00329           case 10:  month.Set(__L("Oct")); break;
00330           case 11:  month.Set(__L("Nov")); break;
00331           case 12:  month.Set(__L("Dec")); break;
00332           default:  break;
00333         }
00334 
00335       //Date: Wed, 21 Oct 2015 07:28:00 GMT
00336       date.Format(__L("%s, %d %s %d %s GMT"), day.Get(), datetime->GetDay(), month.Get(), datetime->GetYear(), hour.Get());
00337       line.Format(__L("Date:%s"), date.Get());  AddLine(line);
00338       delete(datetime);
00339    }
00340 
00341   if(entitytag)
00342     {
00343       if(!entitytag->IsEmpty())
00344         {
00345           line.Format(__L("ETag: \"%s\""), entitytag->Get());
00346           AddLine(line.Get());
00347         }
00348     }
00349 
00350   //---------------------------------------------------------------
00351 
00352   AddLine(__L("Connection: Keep-Alive"));
00353   line.Format(__L("Keep-Alive: timeout=%d,max=%d"), DIOWEBSERVER_KEEPALIVE
00354                                                   , DIOWEBSERVER_KEEPALIVE_MAXRESOURCES);
00355   AddLine(line);
00356 
00357   //---------------------------------------------------------------
00358 
00359   line.Format(__L("Last-Modified: %s"), date.Get());                  AddLine(line);
00360   line.Format(__L("Server: %s "), DIOWEBSERVER_IDSERVER);   AddLine(line);
00361 
00362   if(size != -1)
00363     {
00364       line.Format(__L("Content-Length: %d"), size);
00365       AddLine(line);
00366 
00367     }
00368 
00369   /*
00370   AddLine(__L("Access-Control-Allow-Headers: DNT,X-CustomHeader,Keep-Alive,User-Agent,X-Requested-With,If-Modified-Since,Cache-Control,Content-Type,Content-Encoding,Vary"));
00371   AddLine(__L("Vary : Accept-Encoding"));
00372 
00373   connection->SetActualEncodingFlag(DIOWEBSERVER_ENCODINGFLAGS_NONE);
00374 
00375   bool validsize = false;
00376 
00377   if(size > 100)  validsize = true;
00378   if(size == -1)  validsize = true;
00379 
00380   if(request->IsAcceptedEncodingFlag(DIOWEBSERVER_ENCODINGFLAGS_GZIP))
00381     {
00382       if(webserver->IsIncludedEncodingFlag(DIOWEBSERVER_ENCODINGFLAGS_GZIP))
00383         {
00384           if((validsize) && (isplayable))
00385             {
00386               connection->SetActualEncodingFlag(DIOWEBSERVER_ENCODINGFLAGS_GZIP);
00387               AddLine(__L("Content-Encoding: gzip"));
00388 
00389             }
00390         }
00391     }
00392   */
00393 
00394   return true;
00395 }
