00410 bool DIOWEBSERVER_HEADER::AddWebSocketKey(XSTRING& websocketaccept, XSTRING& websocketprotocol)
00411 {
00412   XSTRING line;
00413 
00414   AddLine(__L("Access-Control-Allow-Headers: x-websocket-version"));
00415   AddLine(__L("Connection: Upgrade"));
00416   AddLine(__L("Upgrade: websocket"));
00417 
00418   line.Format(__L("Sec-WebSocket-Accept: %s"), websocketaccept.Get());
00419   AddLine(line.Get());
00420 
00421   if(websocketprotocol.GetSize())
00422     {
00423       line.Format(__L("Sec-WebSocket-Protocol: %s"), websocketprotocol.Get());
00424       AddLine(line.Get());
00425     }
00426 
00427   return true;
00428 }
