00443 bool DIOWEBSERVER_HEADER::GetDescriptionResult(DIOWEBHEADER_RESULT result, XSTRING& description)
00444 {
00445   typedef struct
00446   {
00447     DIOWEBHEADER_RESULT  result;
00448     XCHAR*               description;
00449 
00450   } DESCRIPTION;
00451 
00452   DESCRIPTION  descriptions[] = { { DIOWEBHEADER_RESULT_CONTINUE                     ,  __L("Continue")                         } ,
