00460                                   { DIOWEBHEADER_RESULT_RESETCONTENT                 ,  __L("Reset Content")                    } ,
00461                                   { DIOWEBHEADER_RESULT_PARTIALCONTENT               ,  __L("Partial Content")                  } ,
00462 
00463                                   { DIOWEBHEADER_RESULT_MULTIPLECHOICES              ,  __L("Multiple Choices")                 } ,
00464                                   { DIOWEBHEADER_RESULT_MOVEDPERMANENTLY             ,  __L("Moved Permanently")                } ,
00465                                   { DIOWEBHEADER_RESULT_FOUND                        ,  __L("Found")                            } ,
00466                                   { DIOWEBHEADER_RESULT_SEEOTHER                     ,  __L("See Other")                        } ,
00467                                   { DIOWEBHEADER_RESULT_NOTMODIFIED                  ,  __L("Not Modified")                     } ,
00468                                   { DIOWEBHEADER_RESULT_USEPROXY                     ,  __L("Use Proxy")                        } ,
00469                                   { DIOWEBHEADER_RESULT_UNUSED                       ,  __L("(Unused)")                         } ,
00470                                   { DIOWEBHEADER_RESULT_TEMPORARYREDIRECT            ,  __L("Temporary Redirect")               } ,
00471 
00472                                   { DIOWEBHEADER_RESULT_BADREQUEST                   ,  __L("Bad Request")                      } ,
00473                                   { DIOWEBHEADER_RESULT_UNAUTHORIZED                 ,  __L("Unauthorized")                     } ,
00474                                   { DIOWEBHEADER_RESULT_PAYMENTREQUIRED              ,  __L("Payment Required")                 } ,
00475                                   { DIOWEBHEADER_RESULT_FORBIDDEN                    ,  __L("Forbidden")                        } ,
