00484                                   { DIOWEBHEADER_RESULT_PRECONDITIONFAILED           ,  __L("Precondition Failed")              } ,
00485                                   { DIOWEBHEADER_RESULT_REQUESTENTITYTOOLARGE        ,  __L("Request Entity Too Large")         } ,
00486                                   { DIOWEBHEADER_RESULT_REQUESTURITOOLONG            ,  __L("Request-URI Too Long")             } ,
00487                                   { DIOWEBHEADER_RESULT_UNSUPPORTEDMEDIATYPE         ,  __L("Unsupported Media Type")           } ,
00488                                   { DIOWEBHEADER_RESULT_REQUESTEDRANGENOTSATISFIABLE ,  __L("Requested Range Not Satisfiable")  } ,
00489                                   { DIOWEBHEADER_RESULT_EXPECTATIONFAILED            ,  __L("Expectation Failed")               } ,
00490 
00491                                   { DIOWEBHEADER_RESULT_INTERNALSERVERERROR          ,  __L("Internal Server Error")            } ,
00492                                   { DIOWEBHEADER_RESULT_NOTIMPLEMENTED               ,  __L("Not Implemented")                  } ,
00493                                   { DIOWEBHEADER_RESULT_BADGATEWAY                   ,  __L("Bad Gateway")                      } ,
