00496                                   { DIOWEBHEADER_RESULT_HTTPVERSIONNOTSUPPORTED      ,  __L("HTTP Version Not Supported")       }
00497                                 };
00498 
00499   description.Empty();
00500 
00501   for(int c=0;c<sizeof(descriptions)/sizeof(DESCRIPTION);c++)
00502     {
00503       if(result == descriptions[c].result)
00504         {
00505           description = descriptions[c].description;
00506 
00507           return true;
00508         }
00509     }
00510 
00511   return false;
