00907 void DIOWEBSERVER_REQUEST::SetIfNoneMatch(XSTRING& ifnonematch)
00908 {
00909   XSTRING special;
00910 
00911   special = ifnonematch;
00912 
00913   special.DeleteCharacter(0x20    , XSTRINGCONTEXT_FROM_FIRST);
00914   special.DeleteCharacter(0x20    , XSTRINGCONTEXT_TO_END);
00915   special.DeleteCharacter(__C('"'), XSTRINGCONTEXT_FROM_FIRST);
00916   special.DeleteCharacter(__C('"'), XSTRINGCONTEXT_TO_END);
00917 
00918   this->ifnonematch = special;
00919 }
