00991 void DIOWEBSERVER_REQUEST::ProcessAcceptedEncodingFlags(XSTRING& string)
00992 {
00993   if(string.IsEmpty()) return;
00994 
00995   //DIEGO: Achtung! you have to calculate the Q = 0 of the headers just in case it is a browser Obsolete
00996 
00997   if(string.Find(__L("gzip"),     false) != NOTFOUND)   acceptedencodingflags |= DIOWEBSERVER_ENCODINGFLAGS_GZIP;
00998   if(string.Find(__L("deflate"),  false) != NOTFOUND)   acceptedencodingflags |= DIOWEBSERVER_ENCODINGFLAGS_DEFLATE;
00999   if(string.Find(__L("compress"), false) != NOTFOUND)   acceptedencodingflags |= DIOWEBSERVER_ENCODINGFLAGS_COMPRESS;
01000   if(string.Find(__L("sdhc"),     false) != NOTFOUND)   acceptedencodingflags |= DIOWEBSERVER_ENCODINGFLAGS_SHAREDDICTIONARY;
01001 }
