01136 bool DIOWEBSERVER_REQUEST::GetLoginPassword(XSTRING& login, XSTRING& password)
01137 {
01138   if(loginpassword.IsEmpty()) return false;
01139 
01140   XSTRING lp;
01141 
01142   lp.ConvertToBase64(loginpassword);
01143   //lp.ConvertFromBase64(loginpassword);
01144 
01145   int index = lp.Find(__L(":"),true);
01146   if(index ==  XSTRING_NOTFOUND) return false;
01147 
01148   lp.Copy(0,index,login);
01149   lp.Copy(index+1,password);
01150 
01151   return true;
01152 }
01153 
