01169 
01181 bool DIOWEBSERVER_REQUEST::ConvertFromWeb(XSTRING& string)
01182 {
01183   int c = 0;
01184 
01185   XCHAR* str = string.Get();
01186 
01187   while(str[c])
01188     {
01189       if(str[c]==__C('%'))
01190         {
01191           XSTRING string2;
01192 
01193           string2.Format(__L("%c%c"),str[c+2],str[c+1]);
01194 
01195           int code = string2.ConvertToInt();
01196           if(code)
01197             {
01198               str[c]=code;
01199 
01200               string.DeleteCharacters(c+1,2);
01201               str = string.Get();
01202             }
01203 
01204         } else c++;
01205     }
01206 
01207   return true;
01208 }
