01296 void DIOWEBSERVER_REQUEST::Clean()
01297 {
01298   type                      = DIOWEBSERVER_TYPE_NONE;
01299   method                    = DIOWEBHEADER_METHOD_UNKNOWN;
01300 
01301   resource.Empty();
01302   guest.Empty();
01303   useragent.Empty();
01304   loginpassword.Empty();
01305   ifnonematch.Empty();
01306 
01307   acceptedcharset.Empty();
01308   acceptedcontrolrequestmethod.Empty();
01309   acceptedlanguage.Empty();
01310   acceptedallowheaders.Empty();
01311 
01312   acceptedencodingflags     = DIOWEBSERVER_ENCODINGFLAGS_NONE;
01313 
01314   accesscontrolalloworigin.Empty();
01315   accesscontrolallowmethods.Empty();
01316   accesscontrolallowheaders.Empty();
01317 
01318   origin.Empty();
01319 
01320   connection.Empty();
01321   upgrade.Empty();
01322 
01323   iskeepalive               = false;
01324 
01325   websocket_isrequest       = false;
01326   websocket_key.Empty();
01327   websocket_protocol.Empty();
01328   websocket_version         = 0;
01329 
01330   size                      = -1;
01331   data                      = NULL;
01332 }
