01382 bool DIOWEBSERVER_CONNECTION::Ini(DIOWEBSERVER* webserver, DIOSTREAMTCPIPCONFIG* diostreamcfg)
01383 {
01384   this->webserver     = webserver;
01385   this->diostreamcfg  = diostreamcfg;
01386 
01387   SetMode(DIOWEBSERVER_CONNECTION_MODE_UNKNOWN);
01388 
01389   GEN_XFACTORY_CREATE(xtimerconnection, CreateTimer())
01390   if(!xtimerconnection)      return false;
01391   
01392   GEN_XFACTORY_CREATE(xtimerdisconnection, CreateTimer())
01393   if(!xtimerdisconnection)   return false;
01394     
01395   nresourcesprocessed = 0;
01396 
01397   diostream = (DIOSTREAMTCPIP*)GEN_DIOFACTORY.CreateStreamIO(diostreamcfg);
01398   if(!diostream)          return false;
01399 
01400   threadconnection   = CREATEXTHREAD(XTHREADGROUPID_DIOWEBSERVER, __L("DIOWEBSERVER_CONNECTION::Ini"), ThreadRunFunction, (void*)this);
01401   if(!threadconnection)   return false;
01402 
01403   return true;
01404 }
