01488   if(!threadconnection) return false;
01489 
01490  // End Server connection page
01491   GetDIOStream()->WaitToFlushOutXBuffer(5);
01492   GetDIOStream()->Disconnect();
01493 
01494   threadconnection->Run(false);
01495 
01496   return true;
