01543 bool DIOWEBSERVER_CONNECTION::Receiver(XBYTE* buffer, XDWORD& size, int timeout)
01544 {
01545   if(!diostream)                return false;
01546   if(!diostream->IsConnected()) return false;
01547   if(!size)                     return false;
01548 
01549   XDWORD br;
01550 
01551   XTIMER* GEN_XFACTORY_CREATE(xtimerout, CreateTimer())
01552   if(!xtimerout)  return false;
01553   
01554   bool status = true;
01555 
01556   while(1)
01557     {
01558       if(!diostream)
01559         {
01560           status = false;
01561           break;
01562         }
01563 
01564       if(!diostream->IsConnected())
01565         {
01566           status = false;
01567           break;
01568         }
01569 
01570       br = diostream->Read(buffer, size);
01571       if(br) break;
01572 
01573       if(xtimerout->GetMeasureSeconds()>= (XDWORD)timeout) break;
01574     }
01575 
01576   GEN_XFACTORY.DeleteTimer(xtimerout);
01577 
01578   if(!br) return false;
01579   size = br;
01580 
01581   return status;
01582 }
01583 
