01598 bool DIOWEBSERVER_CONNECTION::Send(XBYTE* buffer, XDWORD& size, int timeout)
01599 {
01600   if(!diostream) return false;
01601   if(!size)      return false;
01602 
01603   bool status = true;
01604   int  bw     = 0;
01605 
01606   bw = diostream->Write(buffer, size);
01607   if(size != bw) status = false;
01608 
01609   return status;
01610 }
