01925 bool DIOWEBSERVER_CONNECTION::End()
01926 {
01927   if(threadconnection)
01928     {
01929       threadconnection->End();
01930       DELETEXTHREAD(XTHREADGROUPID_DIOWEBSERVER, threadconnection);
01931       threadconnection = NULL;
01932     }
01933 
01934   if(diostream)
01935     {
01936       diostream->Close();
01937 
01938       GEN_DIOFACTORY.DeleteStreamIO(diostream);
01939       diostream = NULL;
01940     }
01941 
01942   if(xtimerconnection)
01943     {
01944       GEN_XFACTORY.DeleteTimer(xtimerconnection);
01945       xtimerconnection = NULL;
01946     }
01947 
01948   if(xtimerdisconnection)
01949     {
01950       GEN_XFACTORY.DeleteTimer(xtimerdisconnection);
01951       xtimerdisconnection = NULL;
01952     }
01953 
01954   isactive            = false;
01955   isrequestinprogress = false;
01956 
01957   return true;
01958 }
01959 
01960 
01970 bool DIOWEBSERVER_CONNECTION::ReadRequest()
01971 {
01972   if(!diostream) return false;
01973 
01974   if(!diostream->IsConnected())                 return false;
01975   if(diostream->GetInXBuffer()->GetSize() < 5)  return false;
01976 
01977   if(!header.Read(diostream, DIOWEBSERVER_DEFAULTCONNECTIONTIMEOUT))  return false;
01978 
01979   XSTRING string;
01980 
01981   string = (*(XSTRING*)(header.GetLines()->Get(0)));
01982 
01983   if(string.Find(__L("HTTP/"),true,4)!=XSTRING_NOTFOUND)
01984     {
01985       request.SetType(DIOWEBSERVER_TYPE_FULL);
01986 
01987     } else request.SetType(DIOWEBSERVER_TYPE_SYMPLE);
01988 
01989   header.GetMethod(string);
01990 
01991   //XTRACE_PRINTCOLOR(1, __L("method: %s"), string.Get());
01992 
01993   request.SetMethod(DIOWEBHEADER_METHOD_UNKNOWN);
01994 
01995   if(!string.Compare(__L("GET")     , true))  request.SetMethod(DIOWEBHEADER_METHOD_GET);
01996   if(!string.Compare(__L("HEAD")    , true))  request.SetMethod(DIOWEBHEADER_METHOD_HEAD);
01997   if(!string.Compare(__L("POST")    , true))  request.SetMethod(DIOWEBHEADER_METHOD_POST);
01998   if(!string.Compare(__L("PUT")     , true))  request.SetMethod(DIOWEBHEADER_METHOD_PUT);
01999   if(!string.Compare(__L("DELETE")  , true))  request.SetMethod(DIOWEBHEADER_METHOD_DELETE);
02000   if(!string.Compare(__L("CONNECT") , true))  request.SetMethod(DIOWEBHEADER_METHOD_CONNECT);
02001   if(!string.Compare(__L("OPTIONS") , true))  request.SetMethod(DIOWEBHEADER_METHOD_OPTIONS);
02002   if(!string.Compare(__L("TRACE")   , true))  request.SetMethod(DIOWEBHEADER_METHOD_TRACE);
02003 
02004   header.GetResource(string);
02005   if(string.GetSize()) request.GetResource()->Set(string);
02006 
02007   string = header.GetFieldValue(__L("Host:"));
02008   request.GetGuest()->Set(string);
02009 
02010   string = header.GetFieldValue(__L("User-Agent:"));
02011   request.GetUserAgent()->Set(string);
02012 
02013   string = header.GetFieldValue(__L("Content-Length:"));
02014   if(!string.IsEmpty()) request.SetSize(string.ConvertToInt());
02015 
02016   string = header.GetFieldValue(__L("If-None-Match:"));
02017   request.SetIfNoneMatch(string);
02018 
02019   string = header.GetFieldValue(__L("Authorization: basic"));
02020   request.SetLoginPassword(string);
02021 
02022   string = header.GetFieldValue(__L("Accept-Encoding:"));
02023   request.ProcessAcceptedEncodingFlags(string);
02024 
02025   string = header.GetFieldValue(__L("Accept-Language:"));
02026   request.GetAcceptedLanguage()->Set(string);
02027 
02028   string = header.GetFieldValue(__L("Accept-Charset:"));
02029   request.GetAcceptedCharset()->Set(string);
02030 
02031   string = header.GetFieldValue(__L("Access-Control-Allow-Origin:"));
02032   request.GetAccessControlAllowOrigin()->Set(string);
02033 
02034   string = header.GetFieldValue(__L("Access-Control-Allow-Methods:"));
02035   request.GetAcceptedControlRequestMethod()->Set(string);
02036 
02037   string = header.GetFieldValue(__L("Access-Control-Allow-Headers:"));
02038   request.GetAccessControlAllowHeaders()->Set(string);
02039 
02040   string = header.GetFieldValue(__L("origin:"));
02041   request.GetOrigin()->Set(string);
02042 
02043   string = header.GetFieldValue(__L("Connection:"));
02044   request.GetConnection()->Set(string);
02045 
02046   string = header.GetFieldValue(__L("Upgrade:"));
02047   request.GetUpgrade()->Set(string);
02048 
02049   string = header.GetFieldValue(__L("Sec-WebSocket-Key:"));
02050   request.WebSocket_GetKey()->Set(string);
02051 
02052   string = header.GetFieldValue(__L("Sec-WebSocket-Protocol:"));
02053   request.WebSocket_GetProtocol()->Set(string);
02054 
02055   string = header.GetFieldValue(__L("Sec-WebSocket-Version:"));
02056   request.WebSocket_SetVersion(string.ConvertToInt());
02057 
02058   if(request.GetSize()>0)
02059     {
02060       XBUFFER* data = request.GetData();
02061       if(!data) return false;
02062 
02063       XDWORD size;
02064 
02065       data->Delete();
02066 
02067       XBYTE* buffer = new XBYTE[DIOWEBSERVER_MAXBUFFER];
02068       if(!buffer) return false;
02069 
02070       do{ memset(buffer,0,DIOWEBSERVER_MAXBUFFER);
02071           size = (request.GetSize() - data->GetSize());
02072           if(size>DIOWEBSERVER_MAXBUFFER) size = DIOWEBSERVER_MAXBUFFER;
02073 
02074           if(size)
02075             {
02076               Receiver((XBYTE*)buffer,size, DIOWEBSERVER_DEFAULTCONNECTIONTIMEOUT);
02077               if(size) data->Add((XBYTE*)buffer,size);
02078             }
02079 
02080         } while(size);
02081 
02082       delete [] buffer;
02083     }
02084 
02085   //----------------------------------------------------------------------------------
02086 
02087   SetMode(DIOWEBSERVER_CONNECTION_MODE_NORMAL);
02088 
02089   if(!request.GetConnection()->Compare(__L("Keep-Alive"), true))
02090     {
02091       SetMode(DIOWEBSERVER_CONNECTION_MODE_KEEPALIVE);
02092     }
02093 
02094   if(!request.GetConnection()->Compare(__L("Upgrade"), true) &&
02095      !request.GetUpgrade()->Compare(__L("websocket")))
02096     {
02097       SetMode(DIOWEBSERVER_CONNECTION_MODE_WEBSOCKET);
02098     }
02099 
02100   return true;
02101 }
02102 
02103 
02113 bool DIOWEBSERVER_CONNECTION::SendRequest()
02114 {
02115   if(!webserver) return false;
02116   if(!diostream) return false;
02117 
02118   if(request.GetMethod() == DIOWEBHEADER_METHOD_UNKNOWN) return false;
02119 
02120   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Server Page INI [%d] %s"), this, nresourcesprocessed, request.GetResource()->Get());
02121 
02122   DIOWEBSERVER_XEVENT xevent(webserver, DIOWEBSERVER_XEVENT_TYPE_REQUEST);
02123   xevent.SetServer(webserver);
02124   xevent.SetConnection(this);
02125   xevent.SetRequest(&request);
02126 
02127   webserver->PostEvent(&xevent);
02128 
02129   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Server Page END [%d] %s"), this, nresourcesprocessed, request.GetResource()->Get());
02130 
02131   nresourcesprocessed++;
02132  
02133   return true;
02134 }
02135 
02136 
02149 bool DIOWEBSERVER_CONNECTION::WebSocket_CreateAcceptKey(XSTRING& key, XSTRING& result)
02150 {
02151   if(key.IsEmpty()) return false;
02152 
02153   #define DIOWEBSERVER_WEBSOCKET_GUID  __L("258EAFA5-E914-47DA-95CA-C5AB0DC85B11")
02154 
02155   XSTRING resultbrute;
02156   bool    status;
02157 
02158   result.Empty();
02159 
02160   resultbrute  = key;
02161   resultbrute += DIOWEBSERVER_WEBSOCKET_GUID;
02162 
02163   result.Empty();
02164 
02165   HASHSHA1* sha1 = new HASHSHA1();
02166   if(!sha1) return false;
02167 
02168   XBUFFER charstr;
02169   
02170   resultbrute.ConvertToASCII(charstr);
02171   status = sha1->Do((XBYTE*)charstr.Get(), resultbrute.GetSize());
02172   
02173   sha1->GetResult()->ConvertToBase64(result);
02174 
02175   delete sha1;
02176 
02177   return status;
02178 }
02179 
02180 
02194 bool DIOWEBSERVER_CONNECTION::WebSocket_CreateHeader(DIOWEBSERVER_WEBSOCKET_OPCODE opcode, XDWORD size, XBUFFER& data)
02195 {
02196   data.Empty();
02197 
02198   XWORD miniheader = 0x8000;
02199 
02200   miniheader |= (opcode << 8);
02201 
02202   if(size >= 65536)
02203     {
02204       miniheader |= 127;
02205       data.Add((XWORD)miniheader);
02206 
02207       XQWORD sizedata = size;
02208       data.Add((XQWORD)sizedata);
02209     }
02210    else
02211     {
02212       if(size >= 256)
02213         {
02214           miniheader |= 126;
02215           data.Add((XWORD)miniheader);
02216 
02217           XWORD sizedata = size;
02218           data.Add((XWORD)sizedata);
02219         }
02220        else
02221         {
02222           miniheader |=  (XBYTE)size;
02223           data.Add((XWORD)miniheader);
02224         }
02225     }
02226 
02227   return true;
02228 }
02229 
02230 
02242 bool DIOWEBSERVER_CONNECTION::Websocket_ResolveConnection(DIOWEBSERVER_REQUEST* request)
02243 {
02244   DIOWEBSERVER_HEADER webserverheader;
02245   XSTRING             resultwebsocketaccept;
02246   bool                status = false;
02247 
02248   if(!WebSocket_CreateAcceptKey((*GetRequest()->WebSocket_GetKey()) , resultwebsocketaccept)) return false;
02249 
02250   webserverheader.Create(GetRequest()->GetResource(), 1, 1,  DIOWEBHEADER_RESULT_SWITCHINGPROTOCOLS, -1, NULL, false);
02251   webserverheader.AddWebSocketKey(resultwebsocketaccept, (*GetRequest()->WebSocket_GetProtocol()));
02252 
02253   status = webserverheader.Write(GetDIOStream(), 5);
02254 
02255   return status;
02256 }
02257 
02258 
02271 bool DIOWEBSERVER_CONNECTION::WebSocket_ReadRequest(DIOWEBSERVER_WEBSOCKET_OPCODE& opcode, XBUFFER& data)
02272 {
02273   opcode = DIOWEBSERVER_WEBSOCKET_OPCODE_RESERVED10;
02274 
02275   if(!diostream)                return false;
02276   if(!diostream->IsConnected()) return false;
02277 
02278   XDWORD  sizetoread = diostream->GetInXBuffer()->GetSize();
02279   if(sizetoread <  sizeof(WEBSOCKET_HEADERFRAME))  return false;
02280 
02281    XWORD                 hf          = 0;
02282    WEBSOCKET_HEADERFRAME headerframe;
02283 
02284    if(!diostream->GetInXBuffer()->Get((XBYTE*)&hf, sizeof(XWORD))) return false;
02285 
02286    if(GEN_XSYSTEM.HardwareUseLittleEndian())  SWAPWORD(hf);
02287 
02288    memcpy(&headerframe, &hf, sizeof(WEBSOCKET_HEADERFRAME));
02289 
02290    XQWORD sizeallmsg      = sizeof(XWORD);
02291    XBYTE  extpayloadsize  = 0;
02292    XQWORD sizedata        = headerframe.size;
02293 
02294    if(sizedata == 127)
02295      {
02296        if(!diostream->GetInXBuffer()->Get((XBYTE*)&sizedata, 8, 2 )) return false;
02297        if(GEN_XSYSTEM.HardwareUseLittleEndian())  SWAPQWORD(sizedata);
02298 
02299        extpayloadsize = sizeof(XQWORD);
02300        sizeallmsg     += sizedata;
02301      }
02302     else
02303      {
02304        if(sizedata == 126)
02305          {
02306             XWORD msize = 0;
02307             if(!diostream->GetInXBuffer()->Get((XBYTE*)&msize, 2, 2 )) return false;
02308             if(GEN_XSYSTEM.HardwareUseLittleEndian())  SWAPWORD(msize);
02309 
02310             sizedata       = msize;
02311             extpayloadsize = sizeof(XWORD);
02312             sizeallmsg    += sizedata;
02313          }
02314         else
02315          {
02316            sizeallmsg += headerframe.size;
02317          }
02318     }
02319 
02320   sizeallmsg += extpayloadsize;
02321   if(headerframe.mask) sizeallmsg += sizeof(XDWORD);
02322 
02323   // Have all frame available to Read?
02324   if(sizetoread < sizeallmsg) return false;
02325 
02326   if(!diostream->GetInXBuffer()->Extract(NULL, 0,  (sizeof(XWORD) + extpayloadsize))) return false;
02327 
02328   XBYTE maskcipher[4];
02329 
02330 
02331   if(headerframe.mask)
02332     {
02333       if(!diostream->Read(maskcipher, sizeof(maskcipher))) return false;
02334     }
02335 
02336   data.Empty();
02337 
02338   if(sizedata)
02339     {
02340       data.Resize((XDWORD)sizedata);
02341 
02342       if(!diostream->Read(data)) return false;
02343 
02344       if(headerframe.mask)
02345         {
02346           for(XDWORD c = 0; c<data.GetSize(); c++)
02347             {
02348               data.Get()[c] ^= maskcipher[c % 4];
02349             }
02350         }
02351     }
02352 
02353   opcode = (DIOWEBSERVER_WEBSOCKET_OPCODE)headerframe.opcode;
02354 
02355   websocket_xbufferframepart.Add(data);
02356 
02357   if(opcode == DIOWEBSERVER_WEBSOCKET_OPCODE_CONTINUATION_FRAME)
02358     {
02359       return false;
02360     }
02361 
02362   if(headerframe.final)
02363     {
02364       data.Empty();
02365       data.Add(websocket_xbufferframepart);
02366 
02367       websocket_xbufferframepart.Empty();
02368 
02369       return true;
02370     }
02371 
02372   return false;
02373 }
02374 
02375 
02385 bool DIOWEBSERVER_CONNECTION::WebSocket_SendEvent_Connected()
02386 {
02387   if(!webserver) return false;
02388   if(!diostream) return false;
02389 
02390   if(request.GetMethod() == DIOWEBHEADER_METHOD_UNKNOWN) return false;
02391 
02392   bool status = false;
02393 
02394   DIOWEBSERVER_XEVENT xevent(webserver, DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_CONNECTED);
02395   xevent.SetServer(webserver);
02396   xevent.SetConnection(this);
02397   xevent.SetRequest(&request);
02398   xevent.SetStatus(false);
02399 
02400   webserver->PostEvent(&xevent);
02401 
02402   if(xevent.GetStatus()) status = Websocket_ResolveConnection(&request);
02403 
02404   GEN_XLOG.AddEntry((status?XLOGLEVEL_INFO:XLOGLEVEL_WARNING), DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Websocket %s %d %s %s")  , this
02405                                                                                                                                               , (status?__L("Connected"):__L("No connected"))
02406                                                                                                                                               , request.WebSocket_GetVersion()
02407                                                                                                                                               , request.WebSocket_GetProtocol()->Get()
02408                                                                                                                                               , request.GetResource()->Get());
02409 
02410   isrequestinprogress = false;
02411 
02412   return true;
02413 }
02414 
02415 
02427 bool DIOWEBSERVER_CONNECTION::WebSocket_SendEvent_ReadData(XBUFFER& data)
02428 {
02429   if(!webserver) return false;
02430   if(!diostream) return false;
02431 
02432   DIOWEBSERVER_XEVENT xevent(webserver, DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_READDATA);
02433   xevent.SetServer(webserver);
02434   xevent.SetConnection(this);
02435   xevent.SetRequest(&request);
02436   xevent.GetReadDataWebSocket()->Empty();
02437   xevent.GetReadDataWebSocket()->Add(data);
02438 
02439   webserver->PostEvent(&xevent);
02440 
02441   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Websocket read data %d %s %s")  , this
02442                                                                                                                           , request.WebSocket_GetVersion()
02443                                                                                                                           , request.WebSocket_GetProtocol()->Get()
02444                                                                                                                           , request.GetResource()->Get());
02445   return true;
02446 }
