02511 
02521 void DIOWEBSERVER_CONNECTION::ThreadRunFunction(void* param)
02522 {
02523   DIOWEBSERVER_CONNECTION* wsconn = (DIOWEBSERVER_CONNECTION*)param;
02524   if(!wsconn) return;
02525 
02526   DIOWEBSERVER_WEBSOCKET_OPCODE opcode      = DIOWEBSERVER_WEBSOCKET_OPCODE_RESERVED10;
02527   bool                          sendrequest = false;
02528 
02529   switch(wsconn->GetMode())
02530     {
02531       case DIOWEBSERVER_CONNECTION_MODE_UNKNOWN       :
02532       case DIOWEBSERVER_CONNECTION_MODE_NORMAL        :
02533       case DIOWEBSERVER_CONNECTION_MODE_KEEPALIVE     : if(wsconn->ReadRequest())
02534                                                           {
02535                                                             wsconn->isrequestinprogress = true;
02536 
02537                                                             switch(wsconn->GetMode())
02538                                                               {
02539                                                                 case DIOWEBSERVER_CONNECTION_MODE_UNKNOWN      :
02540                                                                 case DIOWEBSERVER_CONNECTION_MODE_NORMAL       :
02541                                                                 case DIOWEBSERVER_CONNECTION_MODE_KEEPALIVE    : sendrequest = wsconn->SendRequest();
02542                                                                                                                  if(sendrequest) wsconn->GetTimerDisconnection()->Reset();
02543                                                                                                                  wsconn->isrequestinprogress = false;
02544                                                                                                                  break;
02545 
02546                                                                 case DIOWEBSERVER_CONNECTION_MODE_WEBSOCKET    : sendrequest = wsconn->WebSocket_SendEvent_Connected();
02547                                                                                                                  break;
02548                                                               }                                                          
02549                                                           }
02550 
02551                                                         wsconn->GetTimerDisconnection()->Reset();
02552                                                         break;
02553 
02554       case DIOWEBSERVER_CONNECTION_MODE_WEBSOCKET     : { XBUFFER data;
02555                                                           if(wsconn->WebSocket_ReadRequest(opcode, data))
02556                                                             {
02557                                                               switch(opcode)
02558                                                                 {
02559                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_CONTINUATION_FRAME   : break;
02560 
02561                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_TEXT_FRAME           :
02562                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_BINARY_FRAME         : wsconn->WebSocket_SendEvent_ReadData(data);
02563                                                                                                                             break;
02564 
02565                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_CONNECTION_CLOSE     : break;
02566 
02567                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_PING                 : wsconn->WebSocket_WritePingPong(false, data);
02568                                                                                                                             break;
02569 
02570                                                                   case DIOWEBSERVER_WEBSOCKET_OPCODE_PONG                 : wsconn->WebSocket_SendEvent_Pong(data);
02571                                                                                                                             break;
02572 
02573                                                                                                             default       : break;
02574                                                                }
02575 
02576                                                             }
02577                                                         }
02578                                                        break;
02579     }
02580 
02581 
02582   switch(wsconn->GetMode())
02583     {
02584       case DIOWEBSERVER_CONNECTION_MODE_UNKNOWN       :
02585       case DIOWEBSERVER_CONNECTION_MODE_NORMAL        : if((wsconn->xtimerconnection->GetMeasureSeconds() >= DIOWEBSERVER_KEEPALIVE) || (sendrequest)) 
02586                                                           {
02587                                                             if(wsconn->GetTimerDisconnection()->GetMeasureSeconds() > 5) wsconn->Deactivate();
02588                                                           }
02589                                                         break;
02590 
02591       case DIOWEBSERVER_CONNECTION_MODE_KEEPALIVE     : if((wsconn->xtimerconnection->GetMeasureSeconds() >= DIOWEBSERVER_KEEPALIVE) ||
02592                                                           (wsconn->nresourcesprocessed                    >= DIOWEBSERVER_KEEPALIVE_MAXRESOURCES))     wsconn->Deactivate();
02593                                                         break;
02594 
02595       case DIOWEBSERVER_CONNECTION_MODE_WEBSOCKET     : if(opcode == DIOWEBSERVER_WEBSOCKET_OPCODE_CONNECTION_CLOSE)
02596                                                           {
02597                                                             wsconn->Deactivate();
02598                                                             wsconn->WebSocket_SendEvent_Disconnected();
02599                                                           }
02600                                                         break;
02601     }
02602 
02603 
02604   return;
02605 }
02606 
