02616 void DIOWEBSERVER_CONNECTION::Clean()
02617 {
02618   webserver             = NULL;
02619   diostreamcfg          = NULL;
02620   diostream             = NULL;
02621 
02622   isactive              = false;
02623   mode                  =  DIOWEBSERVER_CONNECTION_MODE_UNKNOWN;
02624   xtimerconnection      = NULL;
02625   xtimerdisconnection   = NULL;
02626   nresourcesprocessed   = 0;
02627   isrequestinprogress   = false;
02628 
02629   threadconnection      = NULL;
02630   actualencodingflag    = DIOWEBSERVER_ENCODINGFLAGS_NONE;
02631 
02632   origin.Empty();
02633 }
