02670 
02685 bool DIOWEBSERVER::Ini(int port, bool doinitialconnectitivitytest, int timeoutserverpage, XSTRING* addrlocal)
02686 {
02687   isactive        = false;
02688   doexit          = false;
02689 
02690   diostreamcfg = new DIOSTREAMTCPIPCONFIG();
02691   if(!diostreamcfg) return false;
02692 
02693   this->port                      = port;
02694   this->timeoutserverpage         = timeoutserverpage;
02695   if(addrlocal) this->addrlocal   = (*addrlocal);
02696 
02697   AddPlayablePageExtension(__L(".HTML"));
02698   AddPlayablePageExtension(__L(".HTM"));
02699 
02700   RegisterEvent(DIOWEBSERVER_XEVENT_TYPE_REQUEST);
02701   RegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_CONNECTED);
02702   RegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_READDATA);
02703   RegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_PONG);
02704   RegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_DISCONNECTED);
02705 
02706   GEN_XFACTORY_CREATE(xtimercontrol, CreateTimer())
02707   if(!xtimercontrol) return false;
02708   
02709   diostreamcfg->SetMode(DIOSTREAMMODE_SERVER);
02710   diostreamcfg->GetLocalIP()->Set(this->addrlocal);
02711   diostreamcfg->SetRemotePort(port);
02712 
02713   isactive = true;
02714 
02715   GEN_XFACTORY_CREATE(xmutexconnections, Create_Mutex())
02716   if(!xmutexconnections) return false;
02717 
02718   bool status = true;
02719 
02720   // ------------------------------------------------------------------------------------
02721   // TEST Can open socket for WEB server
02722 
02723   if(doinitialconnectitivitytest)
02724     {
02725       xtimercontrol->Reset();
02726 
02727       DIOSTREAMTCPIP* diostream = (DIOSTREAMTCPIP*)GEN_DIOFACTORY.CreateStreamIO(diostreamcfg);
02728       if(diostream)
02729         {
02730           if(diostream->Open())
02731             {
02732                     
02733               while(1)
02734                 {
02735                   if(diostream->GetConnectStatus() == DIOSTREAMSTATUS_DISCONNECTED)
02736                     {
02737                       status = false;
02738                       break;
02739                     }
02740 
02741                   if(xtimercontrol->GetMeasureSeconds() > 3) break;
02742                 }
02743 
02744               diostream->Close();
02745             
02746           
02747             } else status = false;
02748 
02749           GEN_DIOFACTORY.DeleteStreamIO(diostream);
02750         }
02751 
02752       if(!status) return false;
02753     }
02754 
02755   // ------------------------------------------------------------------------------------
02756 
02757   xtimercontrol->Reset();
02758 
02759   threadserver = CREATEXTHREAD(XTHREADGROUPID_DIOWEBSERVER, __L("DIOWEBSERVER::Ini"),ThreadRunFunction,(void*)this);
02760   if(!threadserver) return false;
02761 
02762   if(!threadserver->Ini()) return false;
02763 
02764   if(addrlocal) GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE , false , __L("Web Server [%08X]: %s in %s:%d "), this, status?__L("Active"):__L("Disable"),  addrlocal->IsEmpty()?__L("Default"):addrlocal->Get(), port);
02765 
02766   return status;
02767 }
