02824 bool DIOWEBSERVER::IsSendingAnyPage()
02825 {
02826   bool sending = false;
02827 
02828   if(!connections.IsEmpty())
02829     {
02830       for(XDWORD c=0;c<connections.GetSize();c++)
02831         {
02832           DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
02833           if(connection)
02834             {
02835               if(connection->IsRequestInProgress())
02836                 {
02837                   sending = true;
02838                   break;
02839                 }
02840             }
02841         }
02842     }
02843 
02844   return sending;
02845 }
