02857 bool DIOWEBSERVER::UnSubscribeAllPages()
02858 {
02859   if(!connections.IsEmpty())
02860     {
02861       for(XDWORD c=0;c<connections.GetSize();c++)
02862         {
02863           DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
02864           if(connection)
02865             {
02866               UnSubscribeEvent(DIOSTREAMXEVENT_TYPE_CONNECTED    , connection->GetDIOStream());
02867               UnSubscribeEvent(DIOSTREAMXEVENT_TYPE_DISCONNECTED , connection->GetDIOStream());
02868             }
02869         }
02870     }
02871 
02872   return true;
02873 }
