02889 bool DIOWEBSERVER::AddAuthentication(XSTRING& guest, XSTRING& login, XSTRING& password)
02890 {
02891   DIOWEBSERVER_AUTHENTICATION* authentication = GetAuthentication(guest);
02892   if(authentication)
02893     {
02894       authentication->Set(login,password);
02895     }
02896    else
02897     {
02898       authentication = new DIOWEBSERVER_AUTHENTICATION();
02899       if(!authentication) return false;
02900 
02901       authentication->Set(guest,login,password);
02902 
02903       authentications.Add(authentication);
02904     }
02905 
02906   return true;
02907 }
02908 
02909 
02921 DIOWEBSERVER_AUTHENTICATION* DIOWEBSERVER::GetAuthentication(XSTRING& guest)
02922 {
02923   if(authentications.IsEmpty()) return NULL;
02924 
02925   DIOWEBSERVER_AUTHENTICATION* authentication = NULL;
02926 
02927   for(int c=0;c<(int)authentications.GetSize();c++)
02928     {
02929       DIOWEBSERVER_AUTHENTICATION* auth = (DIOWEBSERVER_AUTHENTICATION*)authentications.Get(c);
02930       if(auth)
02931         {
02932           if(auth->GetGuest())
02933             {
02934               XSTRING* tguest = auth->GetGuest();
02935               if(tguest)
02936                 {
02937                   if((*tguest) == guest)
02938                     {
02939                       authentication = auth;
02940                       break;
02941                     }
02942                 }
02943             }
02944         }
02945     }
02946 
02947   return authentication;
02948 }
02949 
02950 
02962 bool DIOWEBSERVER::DelAuthentication(XSTRING& guest)
02963 {
02964   DIOWEBSERVER_AUTHENTICATION* authentication = GetAuthentication(guest);
02965   if(!authentication) return false;
02966 
02967   authentications.Delete(authentication);
02968 
02969   delete authentication;
02970 
02971   return true;
02972 }
