03007 bool DIOWEBSERVER::IsPlayablePage(XCHAR* extension)
03008 {
03009   if(playablepageextensions.IsEmpty()) return false;
03010 
03011   for(int c=0;c<(int)playablepageextensions.GetSize();c++)
03012     {
03013       XSTRING* _extension = (XSTRING*)playablepageextensions.Get(c);
03014       if(_extension)
03015         {
03016           if(!_extension->Compare(extension,true)) return true;
03017         }
03018     }
03019 
03020   return false;
03021 }
