03178 DIOWEBSERVER_CONNECTION* DIOWEBSERVER::Websocket_GetConnection()
03179 {
03180   if(connections.IsEmpty()) return NULL;
03181 
03182   websocket_search_indexconnection = 0;
03183   websocket_search_protocol.Empty();
03184   websocket_search_version         = 0;
03185   websocket_search_resource.Empty();
03186 
03187   return Websocket_GetNextConnection();
03188 }
