03227 DIOWEBSERVER_CONNECTION* DIOWEBSERVER::Websocket_GetConnection(XCHAR* protocol, int version)
03228 {
03229   if(connections.IsEmpty()) return NULL;
03230  
03231   websocket_search_indexconnection = 0;
03232   websocket_search_protocol        = protocol;
03233   websocket_search_version         = version;
03234   websocket_search_resource.Empty();
03235 
03236   return Websocket_GetNextConnection();
03237 }
