03239 
03253 DIOWEBSERVER_CONNECTION* DIOWEBSERVER::Websocket_GetConnection(XCHAR* protocol, int version, XCHAR* resource)
03254 {
03255   if(connections.IsEmpty()) return NULL;
03256 
03257   websocket_search_indexconnection = 0;
03258   websocket_search_protocol        = protocol;
03259   websocket_search_version         = version;
03260   websocket_search_resource        = resource;
03261 
03262   return Websocket_GetNextConnection();
03263 }
03264 
03265 
03275 DIOWEBSERVER_CONNECTION* DIOWEBSERVER::Websocket_GetNextConnection()
03276 {
03277   if(connections.IsEmpty()) return NULL;
03278 
03279   if(websocket_search_indexconnection == -1) return NULL;
03280 
03281   DIOWEBSERVER_CONNECTION* connection    = NULL;
03282   XBYTE                    filter_mask   = 0x00;
03283   XBYTE                    filter_status = 0x00;
03284 
03285   if(websocket_search_protocol.GetSize())  filter_mask  |= 0x01;
03286   if(websocket_search_version)             filter_mask  |= 0x02;
03287   if(websocket_search_resource.GetSize())  filter_mask  |= 0x04;
03288 
03289   if(xmutexconnections) xmutexconnections->Lock();
03290 
03291   for( ; websocket_search_indexconnection<(int)connections.GetSize(); websocket_search_indexconnection++)
03292     {
03293       connection = connections.Get(websocket_search_indexconnection);
03294       if(connection)
03295         {
03296           if(connection->GetMode() == DIOWEBSERVER_CONNECTION_MODE_WEBSOCKET)
03297             {
03298               if(!filter_mask) break;
03299 
03300               filter_status = 0x00;
03301               if(!connection->GetRequest()->WebSocket_GetProtocol()->Compare(websocket_search_protocol))   filter_status |= 0x01;
03302               if(connection->GetRequest()->WebSocket_GetVersion()  == websocket_search_version)            filter_status |= 0x02;
03303               if(!connection->GetRequest()->GetResource()->Compare(websocket_search_resource))             filter_status |= 0x04;
03304 
03305               if(filter_mask == filter_status) break;
03306 
03307             } else connection = NULL;
03308         }
03309     }
03310 
03311   if(xmutexconnections) xmutexconnections->UnLock();
03312 
03313   if(!connection)
03314          websocket_search_indexconnection = -1;
03315     else websocket_search_indexconnection++;
03316 
03317   return connection;
03318 }
03319 
03320 
03336 bool DIOWEBSERVER::Websocket_Write(XCHAR* string, int timeout, XCHAR* protocol, int version, XCHAR* resource)
03337 {
03338   DIOWEBSERVER_CONNECTION* connection;
03339   bool                     status = true;
03340 
03341   connection = Websocket_GetConnection(protocol, version, resource);
03342   while(connection)
03343     {
03344       if(connection->WebSocket_Write(string, timeout)) status = false;
03345 
03346       connection = Websocket_GetNextConnection();
03347     }
03348 
03349   return status;
03350 }
