03421 bool DIOWEBSERVER::Websocket_Ping(XCHAR* string, int timeout, XCHAR* protocol, int version, XCHAR* resource)
03422 {
03423   DIOWEBSERVER_CONNECTION* connection;
03424   bool                     status = true;
03425 
03426   connection = Websocket_GetConnection(protocol, version, resource);
03427   while(connection)
03428     {
03429       if(!connection->WebSocket_WritePingPong(true, string, timeout)) status = false;
03430 
03431       connection = Websocket_GetNextConnection();
03432     }
03433 
03434   return status;
03435 }
