03488 
03498 bool DIOWEBSERVER::End()
03499 {
03500   doexit = true;
03501 
03502   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Disable web server [%08X]."), this);
03503 
03504   UnSubscribeAllPages();
03505 
03506   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false , __L("Delete main thread ... "));
03507 
03508   if(threadserver)
03509     {
03510       threadserver->End();
03511       DELETEXTHREAD(XTHREADGROUPID_DIOWEBSERVER, threadserver);
03512       threadserver = NULL;
03513     }
03514 
03515   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Wait to send all pages ..."));
03516   WaitToSentAllPages();
03517 
03518   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete connections not use ..."));
03519   Connections_DeleteUsed();
03520 
03521   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete connections waiting ..."));
03522   Connections_DeleteWaiting();
03523 
03524   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete all connections ... "));
03525   Connections_DeleteAll();
03526 
03527   if(xmutexconnections)
03528     {
03529       GEN_XFACTORY.Delete_Mutex(xmutexconnections);
03530       xmutexconnections = NULL;
03531     }
03532 
03533   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete resources ... "));
03534 
03535   if(xtimercontrol)
03536     {
03537       GEN_XFACTORY.DeleteTimer(xtimercontrol);
03538       xtimercontrol = NULL;
03539     }
03540 
03541   DeleteAllPlayablePageExtensions();
03542 
03543   DelAllAuthentication();
03544 
03545   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete events ... "));
03546 
03547   DeRegisterEvent(DIOWEBSERVER_XEVENT_TYPE_REQUEST);
03548   DeRegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_CONNECTED);
03549   DeRegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_READDATA);
03550   DeRegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_PONG);
03551   DeRegisterEvent(DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_DISCONNECTED);
03552 
03553   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("Delete stream config ... "));
03554 
03555   if(diostreamcfg)
03556     {
03557       delete diostreamcfg;
03558       diostreamcfg = NULL;
03559     }
03560 
03561   GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("End web server [%08X]."), this);
03562 
03563   return true;
03564 }
03565 
03566 
03576 bool DIOWEBSERVER::WaitToSentAllPages()
03577 {
03578   if(!connections.IsEmpty())
03579     {
03580       if(xtimercontrol)
03581         {
03582           do{ GEN_XSLEEP.MilliSeconds(10);
03583 
03584             } while(IsSendingAnyPage());
03585         }
03586     }
03587 
03588   return false;
03589 }
03590 
03591 
03601 XDWORD DIOWEBSERVER::Connections_GetNWaiting()
03602 {
03603   if(xmutexconnections) xmutexconnections->Lock();
03604 
03605   int nconnectionswait = 0;
03606 
03607   if(!connections.IsEmpty())
03608     {
03609       for(XDWORD c=0;c<connections.GetSize();c++)
03610         {
03611           DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03612           if(connection)
03613             {
03614               if((!connection->IsRequestInProgress())  && (connection->GetDIOStream()->GetConnectStatus() == DIOSTREAMSTATUS_GETTINGCONNECTION))
03615                 {
03616                   nconnectionswait++;
03617                 }
03618             }
03619         }
03620     }
03621 
03622   if(xmutexconnections) xmutexconnections->UnLock();
03623 
03624   return nconnectionswait;
03625 }
03626 
03627 
03637 int DIOWEBSERVER::Connections_GetNConnectionsSendingPage()
03638 {
03639   int nconnections = 0;
03640 
03641   if(!connections.IsEmpty())
03642     {
03643       for(XDWORD c=0;c<connections.GetSize();c++)
03644         {
03645           DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03646           if(connection)
03647             {
03648               if(connection->IsRequestInProgress())
03649                 {
03650                   nconnections++;
03651                 }
03652             }
03653         }
03654     }
03655 
03656   return nconnections;
03657 }
03658 
03659 
03669 bool DIOWEBSERVER::Connections_CreateNew()
03670 {
03671   XDWORD nwaiting = Connections_GetNWaiting();
03672   if(nwaiting >= DIOWEBSERVER_MAXPAGECONNECTIONS)  return false;
03673 
03674   if(xmutexconnections) xmutexconnections->Lock();
03675 
03676   DIOWEBSERVER_CONNECTION* connection = new DIOWEBSERVER_CONNECTION();
03677   if(connection)
03678     {
03679       if(connection->Ini(this, diostreamcfg))
03680         {
03681           if(connection->GetDIOStream()->Open())
03682             {
03683               SubscribeEvent(DIOSTREAMXEVENT_TYPE_CONNECTED    , connection->GetDIOStream());
03684               SubscribeEvent(DIOSTREAMXEVENT_TYPE_DISCONNECTED , connection->GetDIOStream());
03685 
03686               // XTRACE_PRINTCOLOR(1, __L("Create Connexion: [%08X]"), connection);
03687 
03688               connections.Add(connection);
03689 
03690               if(xmutexconnections) xmutexconnections->UnLock();
03691 
03692               return true;
03693             }
03694             else
03695             {
03696               connection->End();
03697               delete connection;
03698             }
03699 
03700         } else delete connection;
03701     }
03702 
03703   if(xmutexconnections) xmutexconnections->UnLock();
03704 
03705   return false;
03706 }
03707 
03708 
03718 bool DIOWEBSERVER::Connections_DeleteUsed()
03719 {
03720   if(xmutexconnections) xmutexconnections->Lock();
03721 
03722   if(!connections.IsEmpty())
03723     {
03724       int c = 0;
03725 
03726       do{ DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03727           if(connection)
03728             {
03729               if((connection->GetDIOStream()->GetConnectStatus() == DIOSTREAMSTATUS_DISCONNECTED) &&  (!connection->IsRequestInProgress()))
03730                 {
03731                   UnSubscribeEvent(DIOSTREAMXEVENT_TYPE_CONNECTED    , connection->GetDIOStream());
03732                   UnSubscribeEvent(DIOSTREAMXEVENT_TYPE_DISCONNECTED , connection->GetDIOStream());
03733 
03734                   // XTRACE_PRINTCOLOR(1, __L("Delete Connexion: [%08X]"), connection);
03735 
03736                   connections.Delete(connection);
03737                   delete connection;
03738 
03739                 } else c++;
03740             }
03741 
03742         } while(c<(int)connections.GetSize());
03743     }
03744 
03745   if(xmutexconnections) xmutexconnections->UnLock();
03746 
03747   return true;
03748 }
03749 
03750 
03760 bool DIOWEBSERVER::Connections_DeleteWaiting()
03761 {
03762   if(xmutexconnections) xmutexconnections->Lock();
03763 
03764   if(!connections.IsEmpty())
03765     {
03766       int c = 0;
03767 
03768       do{ DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03769           if(connection)
03770             {
03771               if((!connection->IsRequestInProgress())  && (connection->GetDIOStream()->GetConnectStatus() == DIOSTREAMSTATUS_GETTINGCONNECTION))
03772                 {
03773                   connections.Delete(connection);
03774                   delete connection;
03775 
03776                 } else c++;
03777             }
03778 
03779         } while(c<(int)connections.GetSize());
03780     }
03781 
03782   if(xmutexconnections) xmutexconnections->UnLock();
03783 
03784   return true;
03785 }
03786 
03787 
03797 bool DIOWEBSERVER::Connections_DeleteAll()
03798 {
03799   if(xmutexconnections) xmutexconnections->Lock();
03800 
03801   if(!connections.IsEmpty())
03802     {
03803       connections.DeleteContents();
03804       connections.DeleteAll();
03805     }
03806 
03807   if(xmutexconnections) xmutexconnections->UnLock();
03808 
03809   return true;
03810 }
