03812 
03823 void DIOWEBSERVER::HandleEvent_DIOStream(DIOSTREAMXEVENT* event)
03824 {
03825   if(!event) return;
03826 
03827   switch(event->GetEventType())
03828     {
03829       case DIOSTREAMXEVENT_TYPE_CONNECTED    : { for(XDWORD c=0; c<connections.GetSize(); c++)
03830                                                   {
03831                                                     DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03832                                                     if(connection)
03833                                                       {
03834                                                         if(connection->GetDIOStream() == event->GetDIOStream())
03835                                                           {
03836                                                             connection->Activate();
03837 
03838                                                             XSTRING IPstring;
03839                                                             connection->GetDIOStream()->GetClientIP()->GetXString(IPstring);
03840                                                             GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Connection Active [%s]"), connection, IPstring.Get());
03841 
03842                                                             break;
03843                                                           }
03844                                                       }
03845                                                   }
03846                                               }
03847                                               break;
03848 
03849       case DIOSTREAMXEVENT_TYPE_DISCONNECTED : { for(XDWORD c=0; c<connections.GetSize(); c++)
03850                                                   {
03851                                                     DIOWEBSERVER_CONNECTION* connection = (DIOWEBSERVER_CONNECTION*)connections.Get(c);
03852                                                     if(connection)
03853                                                       {
03854                                                         if(connection->GetDIOStream() == event->GetDIOStream())
03855                                                           {
03856                                                             XSTRING IPstring;
03857                                                             connection->GetDIOStream()->GetClientIP()->GetXString(IPstring);
03858                                                             GEN_XLOG.AddEntry(XLOGLEVEL_INFO, DIOWEBSERVER_LOGSECTIONID_VERBOSE, false, __L("[%08X] Connection Deactive [%s]"), connection, IPstring.Get());
03859 
03860                                                             break;
03861                                                           }
03862                                                       }
03863                                                   }
03864                                               }
03865                                               break;
03866     }
03867 
03868 }
03869 
03870 
03881 void DIOWEBSERVER::HandleEvent(XEVENT* xevent)
03882 {
03883   if(!xevent) return;
03884 
03885   switch(xevent->GetEventFamily())
03886     {
03887       case XEVENT_TYPE_DIOSTREAM   : { DIOSTREAMXEVENT* event = (DIOSTREAMXEVENT*)xevent;
03888                                       if(!event) return;
03889 
03890                                       HandleEvent_DIOStream(event);
03891                                     }
03892                                     break;
03893 
03894     }
03895 }
03896 
03897 
03907 void DIOWEBSERVER::ThreadRunFunction(void* param)
03908 {
03909   DIOWEBSERVER* webserver = (DIOWEBSERVER*)param;
03910   if(!webserver) return;
03911 
03912   if(!webserver->diostreamcfg) return;
03913 
03914   if(webserver->doexit) return;
03915 
03916   webserver->Connections_DeleteUsed();
03917   webserver->Connections_CreateNew();
03918 }
