03929 void DIOWEBSERVER::Clean()
03930 {
03931   diostreamcfg                      = NULL;
03932   port                              = DIOWEBSERVER_DEFAULTPORT;
03933   timeoutserverpage                 = 0;
03934   xmutexconnections                 = NULL;
03935 
03936   isactive                          = false;
03937   doexit                            = false;
03938   xtimercontrol                     = NULL;
03939   threadserver                      = NULL;
03940 
03941   includedencodingflags             = DIOWEBSERVER_ENCODINGFLAGS_NONE;
03942 
03943   websocket_search_indexconnection  = -1;
03944   websocket_search_protocol.Empty();
03945   websocket_search_version          = 0;
03946   websocket_search_resource.Empty();
03947 }
