00409 GRPBITMAPFILE_TYPE GRPBITMAPFILE::GetTypeFromExtenxion(XCHAR* xpath)
00410 {
00411   XPATH               _xpath;
00412   XSTRING             ext;
00413   GRPBITMAPFILE_TYPE  type = GRPBITMAPFILE_TYPE_UNKNOWN;
00414 
00415   _xpath = xpath;
00416 
00417   _xpath.GetExt(ext);
00418   
00419   if(!ext.Compare(__L(".jpg"),  true))  type = GRPBITMAPFILE_TYPE_JPG; 
00420   if(!ext.Compare(__L(".jpeg"), true))  type = GRPBITMAPFILE_TYPE_JPG;
00421   if(!ext.Compare(__L(".png"),  true))  type = GRPBITMAPFILE_TYPE_PNG;
00422   if(!ext.Compare(__L(".bmp"),  true))  type = GRPBITMAPFILE_TYPE_BMP;
00423   if(!ext.Compare(__L(".tga"),  true))  type = GRPBITMAPFILE_TYPE_TGA;
00424 
00425   return type;
00426 }
